mapper.update:
UPDATE trade
SET tread = tread + 1
WHERE tno = #{tno}

UPDATE member
SET mbalance = mbalance - #{bidPrice}
WHERE muuid = #{muuid}

UPDATE member
SET mbalance = mbalance + #{abidprice}
WHERE muuid = #{abidder}

UPDATE auctionhistory
SET astate = 1
WHERE ano = #{ano}

mapper.select:
SELECT tno
FROM trade
WHERE ttype = 1 AND trade.tauctionstate = 2 AND tdate + INTERVAL 2 DAY <= NOW();

mapper.update:
UPDATE trade
SET tauctionstate = #{endtype}
WHERE trade.ttype = 1 AND tno = #{tno}

UPDATE auctionhistory
SET astate = 0
WHERE ano = #{ano} AND tno = #{tno}
