mapper.update:
UPDATE tradeimage
SET tthumbnail = 0
WHERE timage = #{realFileName}

UPDATE trade
SET tread = tread+1
WHERE tno = #{tno}

mapper.select:
SELECT COUNT(*)
FROM tradeimage
WHERE tno  = #{tno}

SELECT timage
FROM tradeimage
WHERE tno = #{tno}

mapper.update:
UPDATE trade
SET tnormalstate = 3
WHERE tno = #{tno} and muuid=#{muuid}

UPDATE trade
SET ttitle = #{ttitle},  ino = #{category}, tcontent = #{tcontent}, tnormalprice = #{tnormalprice}
where tno =#{tno}

mapper.delete:
DELETE FROM tradeimage
WHERE tno = #{tno} and timage = #{selectedImage0} or timage =#{selectedImage1}
or timage =#{selectedImage2}

mapper.select:
SELECT tnormalstate
from trade
where tno = #{tno}

SELECT mamount
FROM member
WHERE mnickname = #{mnickname}

mapper.update:
UPDATE member
SET mamount = mamount-#{tnormalprice}
WHERE muuid = #{pbuyer}

UPDATE trade
SET tnormalstate = #{state}
WHERE tno = #{tno}

UPDATE member
SET mamount = mamount + #{money}
WHERE mnickname = #{mnickname}

UPDATE payment
SET pstate = #{pstate}, penddate = CURRENT_TIMESTAMP
WHERE tno = #{tno}

UPDATE member
SET mamount = mamount + #{pamount}
WHERE muuid = (SELECT pseller FROM payment WHERE tno=#{tno} AND pstate = 0)

UPDATE payment
SET ${ok}= 1,pstate = 1, penddate = CURRENT_TIMESTAMP, ptradereject = #{who}, ptraderejectreason = #{reason}
WHERE tno = #{tno} and pstate = 2

UPDATE member
SET mamount = mamount + #{tnormalprice}
WHERE muuid = #{buyer}
